// EmpresaCard.js
import React, { useState, useEffect } from 'react';
import GaleriaCard from './GaleriaCard'; // Asegúrate de tener un componente GaleriaCard o adaptarlo a tu necesidad.

function EmpresaCard({ empresa }) {
    const [galerias, setGalerias] = useState([]);

    useEffect(() => {
        const fetchGalerias = async () => {
            try {
                const response = await fetch(`/api/galeria?empresa=${empresa.id}`);
                if (response.ok) {
                    const data = await response.json();
                    setGalerias(data);
                } else {
                    console.error('Error al obtener galerías para la empresa:', empresa.id);
                }
            } catch (error) {
                console.error('Error al obtener galerías:', error);
            }
        };

        fetchGalerias();
    }, [empresa.id]);

    return (
        <div className="empresa-card">
            <h2>{empresa.nombre}</h2>
            <p>{empresa.descripcion}</p>
            <p>{empresa.historia}</p>
            <p>{empresa.fecha}</p>
            {galerias.length > 0 && <h3>Galerías de la empresa:</h3>}
            {galerias.map(galeria => <GaleriaCard key={galeria.id} galeria={galeria} />)}
        </div>
    );
}

export default EmpresaCard;
