import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [message, setMessage] = useState('');
  const [empresas, setEmpresas] = useState([]);
  const [empresaSeleccionada, setEmpresaSeleccionada] = useState(null);

  // Cargar mensaje de bienvenida y lista de empresas
  useEffect(() => {
    fetch('/api')
        .then(res => res.text())
        .then(data => setMessage(data))
        .catch(err => console.error('Error al obtener datos:', err));

    fetch('/api/empresa')
        .then(res => res.json())
        .then(data => setEmpresas(data))
        .catch(err => console.error('Error al obtener empresas:', err));
  }, []);

  // Cargar detalles de una empresa específica cuando se selecciona
  useEffect(() => {
    if (!empresaSeleccionada) return;

    fetch(`/api/empresa?id=${empresaSeleccionada.id}`)
        .then(res => res.json())
        .then(data => setEmpresaSeleccionada(data))
        .catch(err => console.error('Error al obtener detalles de la empresa:', err));
  }, [empresaSeleccionada]);

  return (
    <div className="app">
        <h2>{message}</h2>
        {empresas.map(empresa => (
            <div key={empresa.id} onClick={() => setEmpresaSeleccionada(empresa)}>
                {empresa.nombre}
                {/* Aquí podrías agregar más detalles de la empresa o usar el componente EmpresaCard */}
            </div>
        ))}

        {empresaSeleccionada && (
            <div>
                <h3>Detalles de {empresaSeleccionada.nombre}</h3>
                {/* Aquí puedes mostrar más detalles de la empresa seleccionada */}
            </div>
        )}
    </div>
  );
}

export default App;
