import React, { useState } from 'react';

function GaleriaCard({ galeria }) {
    const [isEditing, setIsEditing] = useState(false);
    const [editedDescripcion, setEditedDescripcion] = useState(galeria.descripcion);
    const [editedImagen, setEditedImagen] = useState(null);

    const handleEditSave = async () => {
        if (isEditing) {
            const formData = new FormData();
            formData.append('descripcion', editedDescripcion);
            if (editedImagen) {
                formData.append('imagen', editedImagen);
            }

            try {
                const response = await fetch(`/api/galeria/${galeria.id}`, {
                    method: 'PUT',
                    body: formData,
                });
    
                const data = await response.json();
                console.log(data);
                // Aquí puedes hacer algo con la respuesta, como actualizar la descripción o la imagen en el estado o mostrar un mensaje de éxito.
    
            } catch (error) {
                console.error('Error al actualizar la descripción o la imagen de la galería:', error);
            }
        }
        setIsEditing(!isEditing);
    };

    return (
        <div className="galeria-card">
            <p>
                {isEditing ? 
                    <textarea value={editedDescripcion} onChange={(e) => setEditedDescripcion(e.target.value)} /> 
                    : galeria.descripcion}
            </p>
            {isEditing ? 
                <input type="file" onChange={(e) => setEditedImagen(e.target.files[0])} /> 
                : <img src={`http://localhost:3001/imagenesdegaleria/${galeria.imagen}`} alt={galeria.imagen} />
            }
            <button onClick={handleEditSave}>{isEditing ? 'Guardar' : 'Editar'}</button>
        </div>
    );
}

export default GaleriaCard;
